import React from 'react';
import "./App.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'weather-icons/css/weather-icons.css';
import Weather from './app_component/weather.component';

//api call to 
const API_key="e783e94c7662e09c899f628e856b5532"

class App extends React.Component{
  constructor(props) 
  {
    super(props)
    this.state = {
      city:undefined,
      country:undefined,
      icon:undefined,
      main:undefined,
      celcius:undefined,
      temp_max:undefined,
      temp_min:undefined,
      description: "",
      error:false 
       }
    this.getWeather()
  }

  calCelcius(temp){
    let cell=Math.floor(temp-273.15)
    return cell
  }
  getWeather=async() =>{
    const api_call=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=${API_key}`)
    const response=await api_call.json()
    console.log(response)
    this.setState({
      city:response.name,
      country:response.sys.country,
      celcius:this.calCelcius(response.main.temp),
      temp_max:this.calCelcius(response.main.temp_max),
      temp_min:this.calCelcius(response.main.temp_min),
      description
    })
  }
  render(){
    return( <div className="App">
    <Weather 
    city={this.state.city} 
    country={this.state.country}
    temp_celsius={this.state.celcius}
    temp_max={this.state.temp_max}
    temp_min={this.state.temp_min}
    description={this.state.description}
    />
  </div>)
  } 
}
export default App;
